import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';



class MeteoPage extends React.Component {


state = {
  city: null,
  forecast: []
}
    componentDidMount(){
      this.getWeather();
    }

    getWeather =  _ => {
      fetch('http://localhost:4000/forecast')
        .then(response => response.json())
        .then(response => this.setState({ city: response.city, forecast: response.data}))
        .catch(err => console.error(err))
    }



  renderWeather = ({code, date, day, high, low, text}) => <div key={date}>On {date} temperatures will be between {low} and </div>

  render() {
    const weather = this.state.forecast;
    const weather_1 = weather.map(({code, date, day, high, low, text}) => (high-32)*5/9);
    return (
      <div className="App">
        <h1> Meteo APP </h1>

        <div>
          <h3> Weather forecast for the next 10 days in {this.state.city}</h3>

          {weather.map(this.renderWeather)}
          {weather_1}
        </div>
      </div>
    );
  }
}





ReactDOM.render(
  <MeteoPage />,
  document.getElementById('root')
);
