import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';



class MeteoPage extends React.Component {


state = {}
    componentDidMount(){
      this.getWeather();
    }

    getWeather =  _ => {
      fetch('http://localhost:4000/forecast')
        .then(response => response.json())
        .then (({ city }) => {
          console.log(city)
        })
        .catch(err => console.error(err))
    }
  render() {


    return (
      <div className="App">
        <h1> Meteo APP </h1>

        <div>
          <h3> Weather forecast for the next 10 days in #city</h3>
            <div>
              {}
            </div>
        </div>
      </div>
    );
  }
}





ReactDOM.render(
  <MeteoPage />,
  document.getElementById('root')
);
