import React from 'react';
import ReactDOM from 'react-dom';


class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.props.onClick(this.state.value)}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}




class MeteoPage extends React.Component {



  constructor(props) {
    super(props);
    this.state = {
      weather:{
        city: 'Braga',
        forecast: []
      },
      value: ''};

    this.handleChange = this.handleChange.bind(this);

  }


handleChange(event) {
    this.setState({value: event.target.value});
  }

  componentDidMount(){
    this.getWeather();
  }

  getWeather =  _ => {
    if(this.state.value)
      fetch('http://localhost:4000/forecast?city='+this.state.value)
        .then(response => response.json())
        .then(response => this.setState({weather:{ city: response.city, forecast: response.data}}))
        .catch(err => console.error(err))
      else

      fetch('http://localhost:4000/forecast?city='+this.state.weather.city)
        .then(response => response.json())
        .then(response => this.setState({weather:{ city: response.city, forecast: response.data}}))
        .catch(err => console.error(err))
    }





  render() {
    const weather = this.state.weather.forecast;

    return (
      <div className="App">
<h1> Meteo APP </h1>

        <div>
          <h3> Weather forecast for the next 10 days in {this.state.weather.city}</h3>

          {weather.map(function({code, date, day, high, low, text}){
            const low_1 = Math.round((low-32)*5/9);
            const high_1 = Math.round((high-32)*5/9);
          return <div key={date}>On {date} temperatures will be between {low_1}ºC and {high_1}ºC. It's expected {text}. </div>;
          })
        }
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>


        <label>
          Name:
          <input type="text" value={this.state.value} onChange={(e) => this.setState({value:e.target.value})} />
        </label>
        <button  type="button" onClick={this.getWeather}>Click Me!</button>


        </div>
      </div>
    );
  }
}





ReactDOM.render(
  <MeteoPage />,
  document.getElementById('root')
);
