import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';



class MeteoPage extends React.Component {


state = {
  city: 'Brag',
  forecast: [],
  new_city: 'Braga'

}


handleChange(event) {
    this.setState({new_city: event.target.value});
  }

 handleSubmit(event) {
   this.setState({city: event.target.value});

  }

  getWeather =  _ => {
    const { city } = this.state.new_city.name;
      fetch('http://localhost:4000/forecast?city='+this.state.new_city.name)
        .then(response => response.json())
        .then(response => this.setState({ city: response.city, forecast: response.data}))
        .catch(err => console.error(err))
    }





  render() {
    const weather = this.state.forecast;

    return (
      <div className="App">
<h1> Meteo APP </h1>

        <div>
          <h3> Weather forecast for the next 10 days in {this.state.city}</h3>

          {weather.map(function({code, date, day, high, low, text}){
            const low_1 = Math.round((low-32)*5/9);
            const high_1 = Math.round((high-32)*5/9);
          return <div key={date}>On {date} temperatures will be between {low_1}ºC and {high_1}ºC. It's expected {text}. </div>;
          })
        }
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>

<form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.new_city} />
        </label>
        <input type="submit" value="Submit" />
      </form>
        </div>
      </div>
    );
  }
}





ReactDOM.render(
  <MeteoPage />,
  document.getElementById('root')
);
